import React from 'react';
import {TextInput,View,StyleSheet} from 'react-native';

const userName = ({name,nameChange,onSubmit}) => {
    return <View style = {styles.bar}>
        <TextInput 
            style={styles.input} 
            placeholder="Search"
            value = {name}
            onChangeText = {newTerm => nameChange(newTerm)}
            onEndEditing = {newTerm => onSubmit(newTerm)} //executes when user presses enter
        />
    </View>
};

const styles = StyleSheet.create({
    bar: {
        backgroundColor: 'grey',
        height: 50,
        borderRadius: 5,
        marginHorizontal: 7,
        marginTop: 10,
        flexDirection: 'row'
    },
    input: {
        flex: 1,
        marginHorizontal: 10,
        fontSize: 18
    }
});

export default userName;
